if ( ! function_exists('getCountryList')){
	function getCountryList(){
		$countries = generateCountriesArray();
		foreach($countries['country'] as $row){
			$data[$row] = $row;
		}
		return $data;
	}
}
if ( ! function_exists('getCountries')){
	function getCountries() {
		$countries = generateCountriesArray();
		$_countries = $countries['country'];
		return $_countries;
	}
}
if ( ! function_exists('getCountryCode')){
	function getCountryCode($country) {
		if (empty($country)) { return ''; }
		$countries = generateCountriesArray();
		$_countries = $countries['country'];
		$country_code = array_search($country, $_countries);
		return (($country_code)?$country_code:'');
	}
}
if ( ! function_exists('getCountryRegions')){
	function getCountryRegions($country_name) {
		if (empty($country_name)) {
			return array();
		} else {
			$countries = generateCountriesArray();
			// try to get country_name by code (if country name is code
			if (isset($countries['country'][$country_name])) {
				$country_name = $countries['country'][$country_name];
			} 
			
			$regions = $countries['regions'];
			return (isset($regions[$country_name])?$regions[$country_name]:array());
		}
	}
}



